class Items extends React.Component{

	constructor(props) {

	  	super(props);
	  	console.log(props);
	}

	state = {}

	render() {
		return (
			<li>
				<p className='product'>{this.props.item.product}</p>
				<input type='button' value='ADD TO CART' align="right" onClick={this.addToCart.bind(this)}/>
				<p className='price'>${this.props.item.price}</p>
			</li>
		);
	}

	addToCart(){
		console.log(this.props.item);
		var key = this.props.item;
		const headers = {'Content-type': 'application/json; charset=UTF-8'}
		fetch(`https://nursery.misfitsmarket.com/api/test/v1/`+this.props.item.i, {method: "POST", body: JSON.stringify({key}), headers})
	}

}


export default Items;
